pragma solidity ^0.4.24;

import "./Users.sol";
import "./Roles.sol";
import "./TradingZones.sol";
import "./trade-restrictions/TradingPeriodRestriction.sol";
import "./trade-restrictions/VolumeRestriction.sol";

/// @title Allows water authorities to issue/revoke water licenses and manage their entitlements/allocations.
/// @author Civic Ledger
contract WaterLicense {

    struct License {
        address licenseeAddress;
        string reference;
        uint256 authorityUserId;
        uint256 tradingZoneId;
        string administrativePlan;
    }

    // Contract dependencies
    Users private _usersContract;
    Roles private _rolesContract;
    TradingZones private _tradingZones;
    TradingPeriodRestriction private _tradingPeriodRestriction;
    VolumeRestriction private _volumeRestriction;

    // License storage
    License[] private _licenses;

    constructor(address usersContractAddress, address rolesContractAddress, address tradingZonesAddress) public {
        _usersContract = Users(usersContractAddress);
        _rolesContract = Roles(rolesContractAddress);
        _tradingZones = TradingZones(tradingZonesAddress);

        _tradingPeriodRestriction = new TradingPeriodRestriction();
        _volumeRestriction = new VolumeRestriction();
    }

    // @dev Ensures that only a water authority can access function
    modifier onlyWaterAuthority() {
        require(_rolesContract.hasRoleByAddress("WaterAuthority", msg.sender), "Only water authorities can issue water licenses");
        _;
    }

    /// @dev Retrieves a license by its index
    /// @param id Id of the license to retrieve
    /// @return License information
    function licenseById(uint256 id) public view returns(address, string, uint256, uint256, string, string, uint256, uint256) {
        License memory license = _licenses[id];
        (string memory tradingZoneName, ) = _tradingZones.tradingZoneById(license.tradingZoneId);
        (uint256 startDate, uint256 endDate) = _tradingPeriodRestriction.tradingPeriodByKey(keccak256(abi.encodePacked("WaterLicense", id)), uint256(0));
        return (
            license.licenseeAddress,
            license.reference,
            license.authorityUserId,
            license.tradingZoneId,
            tradingZoneName,
            license.administrativePlan,            
            startDate, 
            endDate
        );
    }

    /// @dev Retrieves the number of licenses that have been issued
    /// @return License count
    function licenseCount() public view returns(uint256) {
        return _licenses.length;
    }

    /// @dev Issues a new water license to a licensee address
    /// @param licenseeAddress The address of the licensee to issue a license to
    /// @param reference A Government reference for the license
    /// @param tradingZoneId The trading zone that they are entitled to trade within
    /// @param startDate License validity start date - from this date they are able to trade
    /// @param endDate License validity end date - after this date they are not able to trade (can be 0 which means no expiry)
    /// @return License information
    function issueLicense(address licenseeAddress, string reference, uint256 tradingZoneId, uint256 startDate, uint256 endDate, string administrativePlan) public onlyWaterAuthority {
        require(licenseeAddress != address(0), "Must specify an address to issue the license to");
        require(startDate < endDate || endDate == 0, "License start date must be before end date");
        require(_tradingZones.isTradingZone(tradingZoneId), "Trading zone id is not a registered trading zone");

        // get the authorities user id
        (uint256 authorityUserId, bool authorityUserExists) = _usersContract.userByAddress(msg.sender);
        require(authorityUserExists, "Water authority is not a registered user");

        _licenses.push(License(licenseeAddress, reference, authorityUserId, tradingZoneId, administrativePlan));
        _tradingPeriodRestriction.addTradingPeriod(keccak256(abi.encodePacked("WaterLicense", _licenses.length - 1)), startDate, endDate);
    }

    /// @dev Adds an allocation to an existing water license
    /// @param id The index of the license to add the allocation to
    /// @param maxVolume Sets the max volume of water they are allocated to use
    function addAllocation(uint256 id, uint256 maxVolume) onlyWaterAuthority public {
        _volumeRestriction.addMaxVolume(keccak256(abi.encodePacked("WaterLicense", id)), maxVolume, VolumeRestriction.VolumePeriodType.PerYear);
    }

    /// @dev Retrieves an allocation by id
    /// @param id The id of the license to get the allocation
    /// @return Allocation volume per year
    function allocationById(uint256 id) public view returns(uint256) {
        ( , uint256 volume, , , ) = _volumeRestriction.maxVolumeByKey(keccak256(abi.encodePacked("WaterLicense", id)));
        return volume;
    }

    /// @dev Checks whether a licensee can trade
    /// @param id The index of the license to check trade restrictions for
    /// @param traderAddress The address of the trader looking to trade using the licensee    
    /// @param volume The volume of water they want to trade
    /// @return Returns true if they are able to trade, returns false if not able to trade
    function canTrade(uint256 id, address traderAddress, uint256 volume) public view returns(bool) {
        if(id >= _licenses.length) {
            return false;
        }
        bytes32 idHash = keccak256(abi.encodePacked("WaterLicense", id));
        return traderAddress == _licenses[id].licenseeAddress && _tradingPeriodRestriction.canTrade(idHash, now) && _volumeRestriction.canTrade(idHash, volume, now);
    }

}

